templates.template:
${:import(java.nio.file.Files, java.nio.file.Path, java.nio.file.Paths, java.nio.file.StandardCopyOption, java.io.IOException)}	
	
	/**
	 * Method to move file
	 * @param source
	 * @param destination
	 * @throws IOException
	 */
	public static void moveFile(String source, String destination) throws IOException {
		Path src = Paths.get(source);
		Path des = Paths.get(destination);
		Files.move(src, des, StandardCopyOption.REPLACE_EXISTING);
	}
